import { getCurrentInstance, ref } from 'vue';

// 用来获取当前组件实例 

export default {
  __name: 'vue3-017',
  setup(__props, {
    expose
  }) {
    expose();
    const {
      proxy
    } = getCurrentInstance(); // 对象解构赋值
    const proxy1 = getCurrentInstance();
    console.log('getCurrentInstance');
    console.log(proxy);
    console.log(proxy1);
    console.log(proxy1 === proxy.$); // true

    const hello = proxy.$translate('greetings.hello');
    const eha = proxy.$translate('greetings.hengheng');

    // console.log(hello)
    // console.log(eha)
    // 除了数据属性，Vue 实例还暴露了一些有用的实例属性与方法。它们都有前缀 $，以便与用户定义的属性区分开来

    const __returned__ = {
      proxy,
      proxy1,
      hello,
      eha,
      getCurrentInstance,
      ref
    };
    Object.defineProperty(__returned__, '__isScriptSetup', {
      enumerable: false,
      value: true
    });
    return __returned__;
  }
};
