import {
  createStore
} from 'vuex'

/*
Vuex是Vue中的全局状态管理框架，它可以管理应用的所有组件的状态
*/

export default createStore({
  // 创建数据仓库
  state: {
    name: '马云'
  },

  // 使用以下方法调用数据仓库里的数据

  // 同步调用: 是用来修改state
  mutations: {
    // * 不带参的
    // trigger(state) {
    //   console.log('不带参的trigger方法')
    //   state.name = '马化腾'
    // },
    // 带参数的
    trigger(state, val) {
      console.log('带参的trigger方法')
      console.log(state)
      console.log(val)
      state.name = val
    }
  },

  // 异步调用: 处理一些异步操作的函数
  actions: {
    sub(store, val) {
      console.log('******')
      store.commit('trigger')
    }
  },
})
