export default {
  install: (app, options) => {
    console.log('调用插件的install方法')
    console.log(options)
    // 注入一个全局可用的 $translate() 方法
    // $translate('greetings.hello')
    app.config.globalProperties.$translate = (key) => {
      // 获取 `options` 对象的深层属性
      // 使用 `key` 作为索引    ['greetings', 'hello'].reduce
      return key.split('.').reduce((o, i) => {
        console.log('o = ', o)
        console.log('i = ', i)
        if (o) return o[i]
      }, options)
    }
  }
}
