import {
  createApp,
} from 'vue'
import App from './App.vue'
import createStore from './store/index.js'
import  Directives  from './directives'

// 下面两种写法都可以
// import { createStore } from './store/index.js'
// import { createStore } from './store'
const app = createApp(App)
app.use(createStore)
// 自定义全局指令
app.directive('focus', {
  mounted(el) {
    el.focus();
  }
})

app.use(Directives)
app.mount('#app')
