import pin from './pin'

// 自定义指令
const directives = {
  pin,
}

export default {
  install(app, options) {
    console.log('自定义指令, options = ')
    console.log(options)
    Object.keys(directives).forEach((key) => {
      app.directive(key, directives[key])
    })
  },
}
